import React, {useState} from 'react';
import './styles.css';

const Step1 =() => (
  <div>
    <h2>Step1</h2>
    <p>This is the content of Step 1.</p>
  </div>
);

const Step2 =() => (
  <div>
    <h2>Step2</h2>
    <p>This is the content of Step 2.</p>
  </div>
);

const Step3 =() => (
  <div>
    <h2>Step3</h2>
    <p>This is the content of Step 3.</p>
  </div>
);

const Wizard = () => {
  const [step, setStep] = useState(1);

  const nextStep = () =>{
    setStep((prevStep) => Math.max(prevStep - 1, 1));
  };

  const renderStep = () => {
    switch (step) {
      case 1:
        return <step1 />;
      case 2:
        return <step2 />;
      case 3:
        return <step3 /> ;
      default:
        return null; 
    }
  };
  return (
    <div className="wizard-container">
      {renderStep()}
    <div className="navigation-buttons">
      <button onClick={prevStep} disabled={step === 1}>
        previous
      </button>
      <button onClick={nextStep} disabled={step === 3}>
        Next
      </button>
      </div>
    </div>
  );
};

const App = () => (
  <div className='app-container'>
    <h1>Multi-Step wizard</h1>
    <Wizard />
  </div>
);

const mountPoint = document.getElementById('root');
if(mountPoint) {
  const ReactElement = React.createElement(App);
  const renderElement = () => {
    const container = document.createElement('div'); 
    container.innerHTML = '' ;
    mountPoint.appendChild(container);
    container.appendChild(ReactElement);
  };
  renderElement();
}
